INSERT INTO "public"."classes" ("id", "name", "hit_die", "primary_ability", "saving_throws", "skill_proficiencies", "equipment", "features", "spellcasting", "created_at", "updated_at", "source", "armor_proficiencies", "weapon_proficiencies", "tool_proficiencies", "subclasses", "description", "equipment_lado_a", "equipment_lado_b", "level_features", "has_spells", "spell_levels", "spell_slots_levels", "subclasses_details", "skill_count", "has_usage_limit", "usage_type", "usage_value", "usage_recovery", "usage_attribute", "manual_level_increases", "has_dice_increase", "initial_dice", "dice_increases", "has_proficiency_doubling", "proficiency_skill_count", "equipment_lado_a_items", "equipment_lado_b_items", "po_lado_a", "po_lado_b", "equipment_choices", "enabled") VALUES ('e2143645-1a13-4de8-867a-01ec5f2631f6', 'Bárbaro', '12', 'Força', 'Constituição, Força', 'Atletismo, Intimidação, Adestrar Animais, Natureza, Percepção, Sobrevivência', '', '""', '""', '2025-09-22 16:24:15.905745+00', '2025-10-15 17:19:22.322441+00', 'PHB 2024', 'Armaduras Leves, Armaduras Médias, Escudos', 'Armas Simples, Armas Marciais', '', '', 'Bárbaros são combatentes poderosos, impulsionados por forças primitivas do multiverso que se manifestam com sua Fúria. Mais do que uma simples emoção — e não limitada à raiva — essa Fúria é a encarnação da ferocidade de um predador, da fúria de uma tempestade e da turbulência do mar.Alguns Bárbaros personificam sua Fúria como um espírito feroz ou um antepassado reverenciado. Outros a veem como uma conexão com a dor e o sofrimento do mundo, um emaranhado impessoal de magia selvagem ou uma expressão de seu eu mais profundo. Para cada Bárbaro, a Fúria é uma força que alimenta não apenas sua habilidade de combate, mas também seus reflexos aprimorados e sentidos ampliados.Bárbaros frequentemente servem como protetores e líderes em suas comunidades. Eles se entregam de cabeça no perigo para que aqueles sob sua proteção não precisem. Sua coragem diante do perigo torna os Bárbaros perfeitamente adequados para aventuras.', '', '', '[{"name": "Fúria", "level": 1, "usage_type": "Manual por Nível", "description": "[{\\"insert\\":\\"Você pode imbuir-se de um poder primordial chamado Fúria, uma força que lhe concede poder e resiliência extraordinários. Você pode usá-la como uma Ação Bônus se não estiver usando armadura Pesada.\\\\r\\\\n\\\\r\\\\nVocê pode inserir sua Fúria quantas vezes for exibida para o seu nível de Bárbaro na coluna Fúrias da tabela de Características do Bárbaro. Você recupera um uso gasto ao terminar um Descanso Curto e recupera todos os usos gastos ao terminar um Descanso Longo .\\\\r\\\\n\\\\r\\\\nEnquanto ativa, sua Fúria segue as regras abaixo.\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Resistência a danos\\\\r\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\"\\\\nVocê tem resistência a danos contundentes, perfurantes e cortantes.\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Dano de Fúria\\\\r\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\"\\\\nQuando você faz um ataque usando Força — com uma arma ou um Ataque Desarmado — e causa dano ao alvo, você ganha um bônus no dano que aumenta à medida que você ganha níveis como Bárbaro, como mostrado na coluna Dano de Fúria da tabela Características do Bárbaro.\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Vantagem de Força\\\\r\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\"\\\\nVocê tem Vantagem em testes de Força e testes de resistência de Força.\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Sem concentração ou feitiços\\\\r\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\"\\\\nVocê não consegue manter a Concentração e não consegue lançar feitiços.\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Duração\\\\r\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\"\\\\nA Fúria dura até o final do seu próximo turno e termina mais cedo se você estiver usando armadura Pesada ou estiver na condição Incapacitado . Se sua Fúria ainda estiver ativa no seu próximo turno, você pode estendê-la por mais uma rodada seguindo um destes procedimentos:\\\\r\\\\n\\"},{\\"insert\\":\\"Faça uma jogada de ataque contra um inimigo.\\\\r\\",\\"attributes\\":{\\"italic\\":true}},{\\"insert\\":\\"\\\\n\\"},{\\"insert\\":\\"Força um inimigo a fazer um teste de resistência.\\\\r\\",\\"attributes\\":{\\"italic\\":true}},{\\"insert\\":\\"\\\\n\\"},{\\"insert\\":\\"Faça uma Ação Bônus para aumentar sua Fúria.\\\\r\\",\\"attributes\\":{\\"italic\\":true}},{\\"insert\\":\\"\\\\nCada vez que a Fúria é prolongada, ela dura até o final do seu próximo turno. Você pode manter a Fúria por até 10 minutos.\\\\n\\"}]", "usage_value": 2, "usage_recovery": "", "has_usage_limit": true, "usage_attribute": null, "has_dice_increase": false, "manual_level_increases": [{"level": 3, "increase": 3}, {"level": 6, "increase": 4}, {"level": 12, "increase": 5}, {"level": 17, "increase": 6}], "has_proficiency_doubling": false}, {"name": "Defesa sem Armadura", "level": 1, "description": "[{\\"insert\\":\\"Enquanto você não estiver vestindo nenhuma armadura, sua \\"},{\\"insert\\":\\"Classe de Armadura\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" base é igual a 10 mais seus modificadores de Destreza e Constituição. Você pode usar um \\"},{\\"insert\\":\\"Escudo\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" e ainda receber este benefício.\\\\n\\"}]", "has_usage_limit": false, "has_dice_increase": false, "unarmored_defense": {"base": 10, "abilities": ["Destreza", "Constituição"], "allows_shield": true}, "has_proficiency_doubling": false}, {"name": "Maestria em Arma", "level": 1, "usage_type": "Manual por Nível", "description": "[{\\"insert\\":\\"Seu treinamento com armas permite que você use as propriedades de maestria de dois tipos de armas corpo a corpo , simples ou marciais , à sua escolha, como machados grandes e machadinhas . Sempre que terminar um \\"},{\\"insert\\":\\"Descanso Longo\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" , você pode praticar exercícios de armas e mudar uma dessas escolhas.\\\\r\\\\n\\\\r\\\\nAo atingir certos níveis de Bárbaro, você ganha a habilidade de usar as propriedades de maestria de mais tipos de armas, como mostrado na coluna Maestria em Armas da tabela de Características do Bárbaro.\\\\n\\"}]", "usage_value": 2, "usage_recovery": "", "has_usage_limit": true, "usage_attribute": null, "has_dice_increase": false, "manual_level_increases": [{"level": 5, "increase": 3}, {"level": 9, "increase": 4}], "has_proficiency_doubling": false}, {"name": "Ataque Imprudente", "level": 2, "description": "[{\\"insert\\":\\"Você pode deixar de lado toda a preocupação com a defesa para atacar com ferocidade aumentada. Ao fazer sua primeira jogada de ataque no seu turno, você pode decidir atacar imprudentemente. Fazer isso lhe dá \\"},{\\"insert\\":\\"Vantagem\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" em jogadas de ataque usando Força até o início do seu próximo turno, mas jogadas de ataque contra você têm \\"},{\\"insert\\":\\"Vantagem\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" durante esse período.\\\\n\\"}]", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Sentido de Perigo", "level": 2, "description": "[{\\"insert\\":\\"Você adquire uma sensibilidade extraordinária de quando as coisas não estão como deveriam, o que lhe dá um benefício ao desviar de perigos. Você tem \\"},{\\"insert\\":\\"Vantagem\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" em salvaguardas de Destreza, a menos que tenha a \\\\r\\\\ncondição \\"},{\\"insert\\":\\"Incapacitado\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\".\\\\n\\"}]", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Subclasse Bárbaro", "level": 3, "description": "Você ganha uma subclasse de Bárbaro à sua escolha. Uma subclasse é uma especialização que lhe concede habilidades em certos níveis de Bárbaro. Pelo resto da sua carreira, você ganha cada uma das habilidades da sua subclasse que sejam do seu nível de Bárbaro ou inferior.", "has_usage_limit": null, "has_dice_increase": null, "has_proficiency_doubling": null}, {"name": "Conhecimento Primordial", "level": 3, "description": "Você ganha proficiência em outra habilidade de sua escolha da lista de habilidades disponível para Bárbaros no nível 1.\\r\\n\\r\\nAlém disso, enquanto sua Fúria estiver ativa, você pode canalizar poder primordial ao tentar certas tarefas; sempre que fizer um teste de habilidade usando uma das seguintes perícias, você pode fazê-lo como um teste de Força, mesmo que normalmente use uma habilidade diferente: Acrobacia , Intimidação , Percepção , Furtividade ou Sobrevivência . Ao usar esta habilidade, sua Força representa o poder primordial fluindo através de você, aprimorando sua agilidade, porte e sentidos.", "has_usage_limit": null, "has_dice_increase": null, "has_proficiency_doubling": null}, {"name": "Melhoria da Pontuação de Habilidade", "level": 4, "description": "Você ganha o talento Aprimoramento de Valor de Habilidade ou outro talento à sua escolha para o qual você se qualifique. Você ganha essa característica novamente nos níveis 8, 12 e 16 de Bárbaro.", "has_usage_limit": null, "has_dice_increase": null, "has_proficiency_doubling": null}, {"name": "Ataque Extra", "level": 5, "description": "[{\\"insert\\":\\"Você pode atacar duas vezes em vez de uma sempre que realizar a ação \\"},{\\"insert\\":\\"Atacar\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" no seu turno.\\\\n\\"}]", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Movimento rápido", "level": 5, "description": "Sua velocidade aumenta em 3 metros enquanto você não estiver usando armadura pesada.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Instinto Selvagem", "level": 7, "description": "[{\\"insert\\":\\"Seus instintos são tão aguçados que você tem \\"},{\\"insert\\":\\"vantagem\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" em testes de Iniciativa .\\\\n\\"}]", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Ataque Instintivo", "level": 7, "description": "Como parte da Ação Bônus que você realiza para entrar em sua Fúria, você pode mover até metade de sua Velocidade .", "has_usage_limit": null, "has_dice_increase": null, "has_proficiency_doubling": null}, {"name": "Melhoria na pontuação de habilidade", "level": 8, "description": "Você ganha o talento Melhoria no Valor de Habilidade ou outro talento de sua escolha para o qual você se qualifica.", "has_usage_limit": null, "has_dice_increase": null, "has_proficiency_doubling": null}, {"name": "Ataque Brutal", "level": 9, "description": "[{\\"insert\\":\\"Se você usar Ataque Imprudente, poderá abrir mão de qualquer \\"},{\\"insert\\":\\"Vantagem\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" em uma jogada de ataque baseada em Força de sua escolha no seu turno. A jogada de ataque escolhida não deve ter \\"},{\\"insert\\":\\"Desvantagem\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" . Se a jogada de ataque escolhida acertar, o alvo sofre 1d10 de dano adicional do mesmo tipo causado pela arma ou \\"},{\\"insert\\":\\"Ataque Desarmado\\",\\"attributes\\":{\\"underline\\":true}},{\\"insert\\":\\" , e você pode causar um efeito de Ataque Brutal de sua escolha. Você tem as seguintes opções de efeito.\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Golpe Forte\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\" : O alvo é empurrado 4,5 metros para longe de você. Você pode então se mover até metade da sua Velocidade em direção ao alvo sem provocar Ataques de Oportunidade .\\\\r\\\\n\\\\r\\\\n\\"},{\\"insert\\":\\"Golpe no Tendão\\",\\"attributes\\":{\\"bold\\":true}},{\\"insert\\":\\" : A Velocidade do alvoé reduzida em 4,5 metros até o início do seu próximo turno. Um alvo só pode ser afetado por um Golpe no Tendão por vez — o mais recente.\\\\n\\"}]", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Fúria Implacável", "level": 11, "description": "Sua Fúria pode mantê-lo lutando apesar de ferimentos graves. Se você ficar com 0 Pontos de Vida enquanto sua Fúria estiver ativa e não morrer imediatamente, você pode realizar um teste de resistência de Constituição CD 10. Se for bem-sucedido, seus Pontos de Vida mudam para um número igual ao dobro do seu nível de Bárbaro.\\r\\n\\r\\nCada vez que você usa esse recurso após o primeiro, a CD aumenta em 5. Quando você termina um Descan...', 'false', '[]', '[]', '[{"name": "Caminho do Berserker", "features": [{"name": "Frenesi", "level": 3, "description": "Se você usar Ataque Imprudente enquanto sua Fúria estiver ativa, você causa dano extra ao primeiro alvo atingido no seu turno com um ataque baseado em Força. Para determinar o dano extra, role um número de d6s igual ao seu bônus de Dano de Fúria e some-os. O dano é do mesmo tipo da arma ou do Ataque Desarmado usado no ataque."}, {"name": "Fúria sem sentido", "level": 6, "description": "Você tem imunidade às condições Encantado e Amedrontado enquanto sua Fúria estiver ativa. Se você estiver Encantado ou Amedrontado ao entrar em Fúria, a condição cessa."}, {"name": "Retaliação", "level": 10, "description": "Quando você sofre dano de uma criatura que está a até 1,5 metro de você, você pode usar uma Reação para fazer um ataque corpo a corpo contra aquela criatura, usando uma arma ou um Ataque Desarmado ."}, {"name": "Presença Intimidadora", "level": 14, "usage_type": "Por Nível", "description": "Como uma Ação Bônus , você pode aterrorizar outras pessoas com sua presença ameaçadora e poder primordial. Ao fazer isso, cada criatura à sua escolha em uma Emanação de 9 metros originária de você deve realizar um teste de resistência de Sabedoria (CD 8 mais seu modificador de Força e Bônus de Proficiência ). Em caso de falha, a criatura permanece na condição Amedrontada por 1 minuto. Ao final de cada turno da criatura Amedrontada , ela repete o teste, encerrando o efeito sobre si mesma em caso de sucesso.\\r\\n\\r\\nDepois de usar esse recurso, você não poderá usá-lo novamente até terminar um Descanso Longo, a menos que gaste um uso de sua Fúria (nenhuma ação necessária) para restaurar seu uso dele.", "usage_value": 1, "initial_dice": null, "dice_increases": [], "usage_recovery": "Descanso Longo", "has_usage_limit": true, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}], "description": "Canalize a raiva em fúria violenta\\r\\nBárbaros que trilham o Caminho do Berserker direcionam sua Fúria principalmente para a violência. Seu caminho é de fúria desenfreada, e eles vibram no caos da batalha enquanto permitem que sua Fúria os tome e os fortaleça."}, {"name": "Caminho do Coração Selvagem", "features": [{"name": "Falante Animal", "level": 3, "usage_type": null, "description": "Você pode conjurar as magias Sentido das Feras e Falar com Animais , mas apenas como Rituais. Sabedoria é sua habilidade de conjuração para elas.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Fúria da Natureza", "level": 3, "usage_type": null, "description": "Sua Fúria explora o poder primordial dos animais. Sempre que você ativa sua Fúria, você ganha uma das seguintes opções à sua escolha.\\r\\n\\r\\nUrso. Enquanto sua Fúria estiver ativa, você terá Resistência a todos os tipos de dano, exceto Força, Necrótico, Psíquico e Radiante.\\r\\n\\r\\nÁguia. Ao ativar sua Fúria, você pode realizar as ações Desengajar e Correr como parte dessa Ação Bônus . Enquanto sua Fúria estiver ativa, você pode realizar uma Ação Bônus para realizar ambas as ações.\\r\\n\\r\\nLobo. Enquanto sua Fúria estiver ativa, seus aliados têm Vantagem em jogadas de ataque contra qualquer inimigo seu a até 1,5 metro de distância.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Aspecto da Natureza", "level": 6, "usage_type": null, "description": "Você ganha uma das seguintes opções à sua escolha. Sempre que terminar um Descanso Longo , você pode mudar sua escolha.\\r\\n\\r\\nCoruja. Você tem Visão no Escuro com alcance de 18 metros. Se você já tiver Visão no Escuro , seu alcance aumenta em 18 metros.\\r\\n\\r\\nPantera. Você tem uma Velocidade de Escalada igual à sua Velocidade .\\r\\n\\r\\nSalmão. Você tem uma Velocidade de Natação igual à sua Velocidade .", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Orador da Natureza", "level": 10, "usage_type": null, "description": "Você pode conjurar a magia Comungar com a Natureza , mas apenas como um Ritual . Sabedoria é sua habilidade de conjuração para ela.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Poder da Natureza", "level": 14, "usage_type": null, "description": "Sempre que você ativa sua Fúria, você ganha uma das seguintes opções de sua escolha.\\r\\n\\r\\nFalcão. Enquanto sua Fúria estiver ativa, você terá uma Velocidade de Voo igual à sua Velocidade se não estiver usando armadura.\\r\\n\\r\\nLeão. Enquanto sua Fúria estiver ativa, qualquer um dos seus inimigos a até 1,5 metro de você terá Desvantagem em jogadas de ataque contra alvos que não sejam você ou outro Bárbaro que tenha esta opção ativa.\\r\\n\\r\\nAríete. Enquanto sua Fúria estiver ativa, você pode fazer com que uma criatura Grande ou menor tenha a condição Caído ao atingi-la com um ataque corpo a corpo.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}], "description": "Bárbaros que seguem o Caminho do Coração Selvagem se consideram parentes dos animais. Esses Bárbaros aprendem meios mágicos para se comunicar com os animais, e sua Fúria intensifica sua conexão com eles, enchendo-os de poder sobrenatural."}, {"name": "Caminho da Árvore do Mundo", "features": [{"name": "Vitalidade da Árvore", "level": 3, "usage_type": null, "description": "Sua Fúria se conecta à força vital da Árvore do Mundo. Você ganha os seguintes benefícios.\\r\\n\\r\\nSurto de Vitalidade. Ao ativar sua Fúria, você ganha uma quantidade de Pontos de Vida Temporários igual ao seu nível de Bárbaro.\\r\\n\\r\\nForça Vivificante. No início de cada um dos seus turnos, enquanto sua Fúria estiver ativa, você pode escolher outra criatura a até 3 metros de você para ganhar Pontos de Vida Temporários . Para determinar o número de Pontos de Vida Temporários , role um número de d6s igual ao seu bônus de Dano de Fúria e some-os. Se algum desses Pontos de Vida Temporários permanecer quando sua Fúria terminar, eles desaparecem.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Galhos da Árvore", "level": 6, "usage_type": null, "description": "Sempre que uma criatura que você possa ver começar seu turno a até 9 metros de você enquanto sua Fúria estiver ativa, você pode usar uma Reação para invocar galhos espectrais da Árvore do Mundo ao redor dela. O alvo deve ser bem-sucedido em um teste de resistência de Força (CD 8 mais seu modificador de Força e Bônus de Proficiência ) ou será teletransportado para um espaço desocupado que você possa ver a até 1,5 metro de você ou para o espaço desocupado mais próximo que você possa ver. Após o teletransporte do alvo, você pode reduzir sua Velocidade para 0 até o final do turno atual.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Raízes Agredidas", "level": 10, "usage_type": null, "description": "Durante o seu turno, seu alcance aumenta em 3 metros com qualquer arma corpo a corpo que tenha a propriedade Pesada ou Versátil , pois gavinhas da Árvore do Mundo se estendem de você. Ao atingir com tal arma no seu turno, você pode ativar a propriedade de maestria Empurrar ou Derrubar , além de uma propriedade de maestria diferente que esteja usando com aquela arma.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Viaje ao longo da árvore", "level": 14, "usage_type": null, "description": "Quando você ativa sua Fúria e como uma Ação Bônus enquanto sua Fúria estiver ativa, você pode se teletransportar até 18 metros para um espaço desocupado que você possa ver.\\r\\n\\r\\nAlém disso, uma vez por Fúria, você pode aumentar o alcance desse teleporte para 45 metros. Ao fazer isso, você também pode trazer até seis criaturas dispostas que estejam a até 3 metros de você. Cada criatura se teletransporta para um espaço desocupado à sua escolha a até 3 metros do seu espaço de destino.", "...', '2', 'false', null, null, null, null, null, 'false', null, null, 'false', '0', '[{"cost": "10.0", "name": "Pacote do Explorador", "weight": "55.0", "category": "", "quantity": 1}, {"cost": "30.0", "name": "Machado grande", "weight": "7.0", "category": "", "quantity": 4}, {"cost": "5.0", "name": "Machado de mão", "weight": "2.0", "category": "", "quantity": 1}]', '[]', '15', '75', '[]', 'true');