INSERT INTO "public"."classes" ("id", "name", "hit_die", "primary_ability", "saving_throws", "skill_proficiencies", "equipment", "features", "spellcasting", "created_at", "updated_at", "source", "armor_proficiencies", "weapon_proficiencies", "tool_proficiencies", "subclasses", "description", "equipment_lado_a", "equipment_lado_b", "level_features", "has_spells", "spell_levels", "spell_slots_levels", "subclasses_details", "skill_count", "has_usage_limit", "usage_type", "usage_value", "usage_recovery", "usage_attribute", "manual_level_increases", "has_dice_increase", "initial_dice", "dice_increases", "has_proficiency_doubling", "proficiency_skill_count", "equipment_lado_a_items", "equipment_lado_b_items", "po_lado_a", "po_lado_b", "equipment_choices") VALUES ('e3d63af1-9538-40ec-921d-6a55c5534bc6', 'Bardo', '8', 'Carisma', 'Destreza, Carisma', 'Adestrar Animais, Acrobacia, Atuação, Enganação, Furtividade, História, Intimidação, Intuição, Investigação, Medicina, Natureza, Percepção, Persuasão, Prestidigitação, Religião, Sobrevivência, Atletismo, Arcanismo', null, null, '""', '2025-09-27 14:38:29.714279+00', '2025-10-07 19:00:02.965676+00', 'PHB 2024', 'Armaduras Leves', 'Armas Simples', 'Ferramentas de Música', '', 'Invocando magia através de música, dança e versos, os Bardos são peritos em inspirar os outros, aliviar ferimentos, desanimar inimigos e criar ilusões. Eles acreditam que o multiverso foi trazido à existência por meio de palavras pronunciadas e que resquícios das Palavras de Criação ainda ressoam brilham em todos os planos de existência. A magia dos Bardos tenta canalizar essas palavras, que transcendem qualquer idioma.', '', '', '[{"name": "Inspiração Bárdica", "level": 1, "usage_type": "Por Modificador de Atributo", "description": "Você pode inspirar outras pessoas sobrenaturalmente por meio de palavras, música ou dança. Essa inspiração é representada pelo seu dado de Inspiração Bárdica, que é um d6 .\\r\\nUsando Inspiração Bárdica. Como uma Ação Bônus , você pode inspirar outra criatura a até 18 metros de você que possa vê-lo ou ouvi-lo. Essa criatura ganha um dos seus dados de Inspiração Bárdica. Uma criatura só pode ter um dado de Inspiração Bárdica por vez.\\r\\nUma vez dentro da próxima hora, quando a criatura falhar em um Teste D20 , ela pode rolar o dado de Inspiração Bárdica e adicionar o resultado ao d20 , potencialmente transformando a falha em um sucesso. Um dado de Inspiração Bárdica é gasto quando ele é rolado.\\r\\nNúmero de Usos. Você pode conferir um dado de Inspiração Bárdica um número de vezes igual ao seu modificador de Carisma (mínimo de uma vez) e recuperar todos os usos gastos ao terminar um Descanso Longo .\\r\\nEm Níveis Mais Altos. Seu dado de Inspiração Bárdica muda quando você atinge certos níveis de Bardo, conforme mostrado na coluna Dado Bárdico da tabela de Características do Bardo. O dado se torna um d8 no nível 5, um d10 no nível 10 e um d12 no nível 15.", "usage_value": null, "initial_dice": "1d6", "dice_increases": [{"dice": "1d8", "level": 5}, {"dice": "1d10", "level": 10}, {"dice": "1d12", "level": 15}], "usage_recovery": "Descanço longo", "has_usage_limit": true, "usage_attribute": "Carisma", "has_dice_increase": true, "has_proficiency_doubling": false}, {"name": "Conjuração", "level": 1, "description": "Você aprendeu a conjurar magias por meio de suas artes bárdicas. \\nVocê conhece dois truques à sua escolha da lista de magias do Bardo.\\nSempre que você ganha um nível de Bardo, você pode substituir um dos seus truques por outro truque de sua escolha da lista de magias de Bardo .\\r\\nQuando você alcança os níveis 4 e 10 de Bardo, você aprende outro truque de sua escolha na lista de magias de Bardo , como mostrado na coluna Truques da tabela de Características do Bardo.\\r\\nEspaços de Magia. A tabela de Recursos do Bardo mostra quantos espaços de magia você tem para conjurar suas magias de nível 1+. Você recupera todos os espaços gastos ao terminar um Descanso Longo .\\r\\nMagias Preparadas de Nível 1+. Você prepara a lista de magias de nível 1+ disponíveis para você conjurar com esta habilidade. Para começar, escolha quatro magias de nível 1 da lista de magias de Bardo . \\r\\nO número de magias na sua lista aumenta à medida que você ganha níveis de Bardo, conforme mostrado na coluna Magias Preparadas da tabela de Características do Bardo. Sempre que esse número aumentar, escolha magias adicionais da lista de magias de Bardo até que o número de magias na sua lista corresponda ao número na tabela. As magias escolhidas devem ser de um nível para o qual você tenha espaços de magia. Por exemplo, se você for um Bardo de nível 3, sua lista de magias preparadas pode incluir seis magias de níveis 1 e 2 em qualquer combinação.\\r\\nSe outra habilidade de Bardo lhe der magias que você sempre preparou, essas magias não contarão para o número de magias que você pode preparar com essa habilidade, mas essas magias contarão como magias de Bardo para você.\\r\\nAlterando suas magias preparadas. Sempre que você ganha um nível de Bardo, você pode substituir uma magia da sua lista por outra magia de Bardo para a qual você tenha espaços de magia.\\r\\nHabilidade de Conjuração. Carisma é sua habilidade de conjuração para suas magias de Bardo.\\r\\nFoco de Conjuração. Você pode usar um Instrumento Musical como Foco de Conjuração para suas magias de Bardo.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": null}, {"name": "Especialização", "level": 2, "description": "Você obtém Especialização (Dobra a o valor de proficiência) em duas de suas perícias, à sua escolha, nas quais já seja proficiente.\\nNo nível 9 de Bardo, você obtém Especialização em mais duas perícias nas quais já seja proficiente à sua escolha.", "has_usage_limit": false, "has_dice_increase": false, "proficiency_skill_count": 2, "has_proficiency_doubling": true}, {"name": "Faz-tudo", "level": 2, "description": "Você pode adicionar metade do seu Bônus de Proficiência (arredondado para baixo) a qualquer teste de habilidade que você fizer que use uma proficiência de perícia que você não tenha e que não use seu Bônus de Proficiência .\\r\\nPor exemplo, se você fizer um teste de Força ( Atletismo ) e não tiver proficiência em Atletismo , você pode adicionar metade do seu Bônus de Proficiência ao teste.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Subclasse de Bardo", "level": 3, "description": "Você ganha uma subclasse de Bardo à sua escolha. Uma subclasse é uma especialização que lhe concede características em determinados níveis de Bardo. Pelo resto da sua carreira, você ganha cada uma das características da sua subclasse que sejam do seu nível de Bardo ou inferior.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Melhoria da Pontuação de Habilidade", "level": 4, "description": "Você ganha o talento Aprimoramento de Valor de Habilidade ou outro talento à sua escolha para o qual você se qualifique. Você ganha essa característica novamente nos níveis 8, 12 e 16 de Bardo.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Fonte de Inspiração", "level": 5, "description": "Agora você recupera todos os seus usos gastos de Inspiração Bárdica quando termina um Descanso Curto ou Longo .\\r\\nAlém disso, você pode gastar um espaço de magia (nenhuma ação necessária) para recuperar um uso gasto de Inspiração Bárdica.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Característica de subclasse", "level": 6, "description": "Você ganha uma característica da sua Subclasse de Bardo.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Contrafeitiço", "level": 7, "description": "Você pode usar notas musicais ou palavras de poder para interromper efeitos de influência mental. Se você ou uma criatura a até 9 metros de você falhar em um teste de resistência contra um efeito que aplique a condição Encantado ou Amedrontado , você pode usar uma Reação para fazer com que o teste seja repetido, e o novo teste terá Vantagem .", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Melhoria na pontuação de habilidade", "level": 8, "description": "Você ganha o talento Melhoria no Valor de Habilidade ou outro talento de sua escolha para o qual você se qualifica.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Especialização", "level": 9, "description": "Você ganha Especialização em duas das Proficiências de Habilidade de sua escolha.", "has_usage_limit": false, "has_dice_increase": false, "proficiency_skill_count": 2, "has_proficiency_doubling": true}, {"name": "Segredos Mágicos", "level": 10, "description": "Você aprendeu segredos de várias tradições mágicas. Sempre que você atingir um nível de Bardo (incluindo este nível) e o número de Magias Preparadas na tabela de Características do Bardo aumentar, você poderá escolher qualquer uma das suas novas magias preparadas das listas de magias de Bardo , Clérigo , Druida e Mago , e as magias escolhidas contarão como magias de Bardo para você (consulte a seção de cada classe para ver sua lista de magias). Além disso, sempre que você substituir uma magia preparada para esta classe, poderá substituí-la por uma magia dessas listas.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Melhoria na Pontuação de Habilidade", "level": 12, "description": "Você ganha o talento Melhoria no Valor de Habilidade ou outro talento de sua escolha para o qual você se qualifica.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Característica de Subclasse", "level": 14, "description": "Você ganha uma característica da sua Subclasse de Bardo.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Melhoria na Pontuação de Habilidade", "level": 16, "description": "Você ganha o talento Melhoria no Valor de Habilidade ou outro talento de sua escolha para o qual você se qualifica.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Inspiração Superior", "level": 18, "description": "Ao rolar Iniciativa , você recupera os usos gastos de Inspiração Bárdica até que você tenha dois, se tiver menos que isso.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Bênção Épica", "level": 19, "description": "Você ganha um talento Dádiva Épica ou outro talento à sua escolha para o qual você se qualifique. Dádiva de Recordação de Magia é recomendada.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}, {"name": "Palavras da Criação", "level": 20, "description": "Você domina duas das Palavras da Criação: as palavras da vida e da morte. Portanto, você sempre tem as magias Palavra de Poder de Cura e Palavra de Poder de Morte preparadas. Ao conjurar qualquer uma das magias, você pode escolher uma segunda criatura como alvo, desde que ela esteja a até 3 metros do primeiro alvo.", "has_usage_limit": false, "has_dice_increase": false, "has_proficiency_doubling": false}]', 'true', '[{"level": 1, "cantrips": 2, "known_spells": 4}, {"level": 2, "cantrips": 2, "known_spells": 5}, {"level": 3, "cantrips": 2, "known_spells": 6}, {"level": 4, "cantrips": 3, "known_spells": 7}, {"level": 5, "cantrips": 3, "known_spells": 9}, {"level": 6, "cantrips": 3, "known_spells": 10}, {"level": 7, "cantrips": 3, "known_spells": 11}, {"level": 8, "cantrips": 3, "known_spells": 12}, {"level": 9, "cantrips": 3, "known_spells": 14}, {"level": 10, "cantrips": 4, "known_spells": 15}, {"level": 11, "cantrips": 4, "known_spells": 16}, {"level": 12, "cantrips": 4, "known_spells": 16}, {"level": 13, "cantrips": 4, "known_spells": 17}, {"level": 14, "cantrips": 4, "known_spells": 17}, {"level": 15, "cantrips": 4, "known_spells": 18}, {"level": 16, "cantrips": 4, "known_spells": 18}, {"level": 17, "cantrips": 4, "known_spells": 19}, {"level": 18, "cantrips": 4, "known_spells": 20}, {"level": 19, "cantrips": 4, "known_spells": 21}, {"level": 20, "cantrips": 4, "known_spells": 22}]', '[{"level": 1, "level_1": 2}, {"level": 2, "level_1": 3}, {"level": 3, "level_1": 4, "level_2": 2}, {"level": 4, "level_1": 4, "level_2": 3}, {"level": 5, "level_1": 4, "level_2": 3, "level_3": 2}, {"level": 6, "level_1": 4, "level_2": 3, "level_3": 3}, {"level": 7, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 1}, {"level": 8, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 2}, {"level": 9, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 1}, {"level": 10, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2}, {"level": 11, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1}, {"level": 12, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1}, {"level": 13, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1, "level_7": 1}, {"level": 14, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1, "level_7": 1}, {"level": 15, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1, "level_7": 1, "level_8": 1}, {"level": 16, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1, "level_7": 1, "level_8": 1}, {"level": 17, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 2, "level_6": 1, "level_7": 1, "level_8": 1, "level_9": 1}, {"level": 18, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 3, "level_6": 1, "level_7": 1, "level_8": 1, "level_9": 1}, {"level": 19, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 3, "level_6": 2, "level_7": 1, "level_8": 1, "level_9": 1}, {"level": 20, "level_1": 4, "level_2": 3, "level_3": 3, "level_4": 3, "level_5": 3, "level_6": 2, "level_7": 2, "level_8": 1, "level_9": 1}]', '[{"name": "Faculdade de Dança", "features": [{"name": "Footwork deslumbrante", "level": 3, "usage_type": null, "description": "Enquanto você não estiver usando armadura ou empunhando um Escudo , você ganha os seguintes benefícios.\\r\\n\\r\\nVirtuoso da Dança. Você tem Vantagem em qualquer teste de Carisma ( Apresentação ) que envolva dança.\\r\\n\\r\\nDefesa sem armadura. Sua Classe de Armadura baseé igual a 10 mais seus modificadores de Destreza e Carisma.\\r\\n\\r\\nAtaques Ágeis. Quando você gasta um uso de sua Inspiração Bárdica como parte de uma ação, uma Ação Bônus ou uma Reação , você pode realizar um Ataque Desarmado como parte dessa ação, Ação Bônus ou Reação .\\r\\n\\r\\nDano de Bardo. Você pode usar Destreza em vez de Força para as jogadas de ataque dos seus Ataques Desarmados. Ao causar dano com um Ataque Desarmado , você pode causar dano de Concussão igual a uma jogada do seu dado de Inspiração de Bardo mais o seu modificador de Destreza, em vez do dano normal do ataque. Esta jogada não gasta o dado.", "usage_value": null, "initial_dice": null, "dice_increases": [], "usage_recovery": null, "has_usage_limit": false, "usage_attribute": null, "has_dice_increase": false, "unarmored_defense": {"base": 10, "abilities": ["Destreza", "Carisma"], "allows_shield": false}, "has_hit_point_increase": false, "additional_feature_name": null, "has_additional_features": false, "hit_point_increase_per_level": null, "additional_feature_description": null}, {"name": "Movimento Inspirador", "level": 6, "description": "Quando um inimigo visível termina seu turno a até 1,5 metro de você, você pode realizar uma Reação e gastar um uso de sua Inspiração Bárdica para mover até metade de sua Velocidade . Então, um aliado de sua escolha a até 9 metros de você também pode mover até metade de sua Velocidade usando sua Reação .\\r\\n\\r\\nNenhum movimento deste recurso provoca Ataques de Oportunidade ."}, {"name": "Trabalho de pés em tandem", "level": 6, "description": "Ao rolar Iniciativa , você pode gastar um uso da sua Inspiração Bárdica se não tiver a condição Incapacitado . Ao fazer isso, role seu dado de Inspiração Bárdica; você e cada aliado a até 9 metros de distância que possa vê-lo ou ouvi-lo ganham um bônus de Iniciativa igual ao número rolado."}, {"name": "Liderando a Evasão", "level": 14, "description": "Quando você é submetido a um efeito que lhe permite fazer um teste de resistência de Destreza para receber apenas metade do dano, você não sofre dano se for bem-sucedido no teste e apenas metade do dano se falhar. Se qualquer criatura a até 1,5 metro de você estiver fazendo o mesmo teste de resistência de Destreza, você pode compartilhar esse benefício com ela para aquele teste.\\r\\n\\r\\nVocê não pode usar esse recurso se tiver a condição Incapacitado ."}], "description": "Os Bardos da Faculdade de Dança sabem que as Palavras da Criação não podem ser contidas na fala ou na música; as palavras são proferidas pelos movimentos dos corpos celestes e fluem através dos movimentos das menores criaturas. Esses Bardos praticam uma maneira de estar em harmonia com o cosmos em movimento que enfatiza a agilidade, a velocidade e a graça."}, {"name": "Faculdade de Glamour", "features": [{"name": "Magia Sedutora", "level": 3, "description": "Você sempre tem os feitiços Encantar Pessoa e Imagem Espelhada preparados.\\r\\n\\r\\nAlém disso, imediatamente após conjurar uma magia de Encantamento ou Ilusão usando um espaço de magia, você pode fazer com que uma criatura visível a até 18 metros de você realize um teste de resistência de Sabedoria contra sua CD de resistência à magia. Em caso de falha, o alvo permanece na condição Encantado ou Amedrontado (à sua escolha) por 1 minuto. O alvo repete o teste ao final de cada um de seus turnos, encerrando o efeito sobre si mesmo em caso de sucesso.\\r\\n\\r\\nApós usar este benefício, você não poderá usá-lo novamente até terminar um Descanso Longo . Você também pode restaurar seu uso gastando um uso da sua Inspiração Bárdica (nenhuma ação necessária)."}, {"name": "Manto da Inspiração", "level": 3, "description": "Você pode incorporar magia feérica em uma canção ou dança para encher os outros de vigor. Como uma Ação Bônus , você pode gastar um uso de Inspiração Bárdica, rolando um dado de Inspiração Bárdica. Ao fazer isso, escolha um número de outras criaturas a até 18 metros de você, até um número igual ao seu modificador de Carisma (mínimo de uma criatura). Cada uma dessas criaturas ganha um número de Pontos de Vida Temporários igual a duas vezes o número rolado no dado de Inspiração Bárdica, e então cada uma pode usar sua Reação para se mover até sua Velocidade sem provocar Ataques de Oportunidade ."}, {"name": "Manto da Majestade", "level": 6, "description": "Você sempre tem o feitiço Comando preparado.\\r\\n\\r\\nComo uma Ação Bônus , você conjura Comandar sem gastar um espaço de magia e assume uma aparência sobrenatural por 1 minuto ou até que sua Concentração termine. Durante esse tempo, você pode conjurar Comandar como uma Ação Bônus sem gastar um espaço de magia.\\r\\n\\r\\nQualquer criatura encantada por você falha automaticamente no teste de resistência contra o Comando que você conjurou com esta habilidade.\\r\\n\\r\\nApós usar esta habilidade, você não poderá usá-la novamente até terminar um Descanso Longo . Você também pode restaurar seu uso gastando um espaço de magia de nível 3+ (nenhuma ação necessária)."}, {"name": "Majestade Inquebrável", "level": 14, "description": "Como uma Ação Bônus , você pode assumir uma presença magicamente majestosa por 1 minuto ou até atingir a condição Incapacitado . Durante esse período, sempre que qualquer criatura o atingir com uma jogada de ataque pela primeira vez em um turno, o atacante deve obter sucesso em um teste de resistência de Carisma contra sua CD de magia, ou o ataque erra, pois a criatura recua diante de sua majestade.\\r\\n\\r\\nDepois de assumir essa presença majestosa, você não poderá fazê-lo novamente até terminar um Descanso Curto ou Longo ."}], "description": "O Colégio do Glamour tem suas origens na magia sedutora da Agrestia das Fadas. Bardos que estudam essa magia tecem fios de beleza e terror em suas canções e histórias, e os mais poderosos entre eles podem se revestir de uma majestade sobrenatural. Suas apresentações despertam uma saudade melancólica da inocência esquecida, evocam memórias inconscientes de medos antigos e tocam as emoções até dos ouvintes mais insensíveis."}, {"name": "Faculdade de Conhecimento", "features": [{"name": "Proficiências Bônus", "level": 3, "description": "Você ganha proficiência em três habilidades de sua escolha."}, {"name": "Cortando Palavras", "level": 3, "description": "Você aprende a usar sua astúcia para distrair, confundir e, de forma sobrenatural, minar a confiança e a competência dos outros. Quando uma criatura que você possa ver a até 18 metros de você fizer uma jogada de dano ou for bem-sucedida em um teste de habilidade ou ataque, você pode usar uma Reação para gastar um uso da sua Inspiração Bárdica; role seu dado de Inspiração Bárdica e subtraia o resultado da jogada da criatura, reduzindo o dano ou potencialmente transformando o sucesso em fracasso."}, {"name": "Descobertas Mágicas", "level": 3, "description": "Você aprende duas magias à sua escolha. Essas magias podem vir da lista de magias de Clérigo , Druida ou Mago, ou de qualquer combinação delas (consulte a seção de cada classe para ver a lista de magias). A magia escolhida deve ser um truque mágico ou uma magia para a qual você tenha espaços de magia, conforme mostrado na tabela de Características do Bardo.\\r\\n\\r\\nVocê sempre tem as magias escolhidas preparadas e, sempre que ganhar um nível de Bardo, poderá substituir uma das magias por outra que atenda a esses requisitos."}, {"name": "Habilidade Inigualável", "level": 14, "description": "Ao fazer um teste de habilidade ou uma jogada de ataque e falhar, você pode gastar um uso de Inspiração Bárdica; role o dado de Inspiração Bárdica e adicione o resultado ao d20 , potencialmente transformando uma falha em um sucesso. Em caso de falha, a Inspiração Bárdica não é gasta."}], "description": "Explore as profundezas do conhecimento mágico\\r\\n\\r\\nOs bardos do Colégio do Conhecimento coletam magias e segredos de diversas fontes, como tomos acadêmicos, ritos místicos e contos camponeses. Os membros do colégio se reúnem em bibliotecas e universidades para compartilhar seus conhecimentos. Eles também se reúnem em festivais ou assuntos de estado, onde podem expor corrupção, desvendar mentiras e zombar de figuras de autoridade presunçosas."}, {"name": "Faculdade da Valentia", "features": [{"name": "Inspiração de Combate", "level": 3, "description": "Você pode usar sua astúcia para mudar o rumo da batalha. Uma criatura que tenha um dado de Inspiração Bárdica seu pode usá-lo para um dos seguintes efeitos.\\r\\n\\r\\nDefesa. Quando a criatura é atingida por uma jogada de ataque, ela pode usar sua Reação para rolar o dado de Inspiração Bárdica e adicionar o valor obtido à sua CA contra aquele ataque, potencialmente fazendo com que o ataque erre.\\r\\n\\r\\nAtaque. Imediatamente após a criatura atingir um alvo com uma jogada de ataque, ela pode rolar o dado de Inspiração Bárdica e adicionar o valor obtido ao dano do ataque contra o alvo."}, {"name": "Treinamento Marcial", "level": 3, "description": "Você ganha proficiência com armas marciais e treinamento com armaduras médias e escudos .\\r\\n\\r\\nAlém disso, você pode usar uma arma Simples ou Marcial como Foco de Conjuração para conjurar magias da sua lista de magias de Bardo ."}, {"name": "Ataque Extra", "level": 6, "description": "Você pode atacar duas vezes em vez de uma sempre que realizar a ação Atacar no seu turno.\\r\\n\\r\\nAlém disso, você pode conjurar um dos seus truques que tenha um tempo de conjuração de uma ação no lugar de um desses ataques."}, {"name": "Magia de Batalha", "level": 14, "description": "Depois de conjurar uma magia que tenha um tempo de conjuração de uma ação, você pode fazer um ataque com uma arma como uma Ação Bônus ."}], "description": "Cante os feitos dos heróis antigos\\r\\n\\r\\nOs Bardos do Colégio da Bravura são contadores de histórias ousados, cujas histórias preservam a memória dos grandes heró...', '3', 'false', null, null, null, null, null, 'false', null, null, 'false', '0', '[{"cost": "40.0", "name": "Pacote do Artista", "weight": null, "category": "", "quantity": 1}, {"cost": "10.0", "name": "Armadura de couro", "weight": "10.0", "category": "", "quantity": 1}, {"cost": "2.0", "name": "Adaga", "weight": "1.0", "category": "", "quantity": 2}]', '[]', '19', '90', '[{"options": [{"cost": "30.0", "name": "Viola", "weight": "1.0", "category": ""}, {"cost": "6.0", "name": "Tambor", "weight": "3.0", "category": ""}, {"cost": "2.0", "name": "Shawm", "weight": "1.0", "category": ""}, {"cost": "25.0", "name": "Saltério", "weight": "10.0", "category": ""}, {"cost": "31.0", "name": "Lira", "weight": "2.0", "category": ""}, {"cost": "30.0", "name": "Gaita de fole", "weight": "6.0", "category": ""}, {"cost": "12.0", "name": "Flauta de Pã", "weight": "2.0", "category": ""}, {"cost": "2.0", "name": "Flauta", "weight": "1.0", "category": ""}, {"cost": "5.0", "name": "Cobertor", "weight": "3.0", "category": ""}, {"cost": "3.0", "name": "Buzina", "weight": "2.0", "category": ""}, {"cost": "35.0", "name": "Alaúde", "weight": "2.0", "category": ""}], "description": "1 intrumento musical"}]');